INSERT INTO Религии (Название, Словесн_опис_символа, Справ_информация, Время_появления, Время_исчезновения) VALUES
  ('Старые боги', 'Чардрево', 'Старые боги — безымянные и безликие духи природы, которым поклоняются на Севере', 'Рассветная эпоха', 'Не наступило'),
  ('Семеро', 'Семиконечная звезда', 'Вера в Семерых является самой распространенной и официальной религией объединенного Вестероса', DEFAULT, 'Не наступило'),
  ('Культ Утонувшего Бога', DEFAULT, 'Дуалистическая религия железнорожденных', DEFAULT, 'Не наступило'),
  ('Культ Р''глора', 'Огонь', 'Культ Р''глора — агрессивная прозелитическая религия, проповедующая борьбу света со тьмой и считающая всех других богов ложными', DEFAULT, 'Не наступило'),
  ('Культ Многоликого', DEFAULT, 'Культ Многоликого берёт своё начало в рудниках Валирии, где в нечеловеческих условиях трудились рабы самых разных национальностей и рас. Первый Безликий, слышавший молитвы этих рабов, понял, что все они просят одного бога, хоть и называют его разными именами, об избавлении от страданий - смерти', DEFAULT, 'Не наступило'),
  ('Культ Великого пастуха', DEFAULT, 'Культ Великого пастуха — религия лхазарян. По представлению верующих все люди мира — одно большое стадо', DEFAULT, 'Не наступило'),
  ('Культ Боаша', DEFAULT, 'Жрецы Боаша проповедовали, что жизнь священна и вечна, что все люди, независимо от пола и сословия, равны перед Богом', '1422 до З.Э.', DEFAULT),
  ('Религия в И-Ти', DEFAULT, 'В И-Ти поклоняются многим богам, известные из которых — Лев Ночи, Дева-из-Света и Бог-на-Земле', DEFAULT, 'Не наступило'),
  ('Церковь Звездной Премудрости', DEFAULT, 'Церковь Звездной Премудрости — зловещая религия, адепты которой встречаются во многих портовых городах мира. По-видимому основана на поклонении упавшему с неба камню. Ее создание многими приписывается Императору Кровавой Яшмы', DEFAULT, 'Не наступило'),
  ('Культ Чёрного Козла', DEFAULT, 'Чёрный Козёл, требует ежедневных кровавых жертвоприношений. Чаще всего в качестве жертвы на алтаре оказываются телята, бычки и лошади, а в дни священных праздников под нож жрецов в капюшонах идут преступники. Во времена кризисов и опасностей городская знать приносила в жертву собственных детей, чтобы ублажить бога, который мог защитить город', DEFAULT, 'Не наступило'),
  ('Религия ройнаров', DEFAULT, DEFAULT, DEFAULT, 'Не наступило'),
  ('Культ Владыки Гармонии', DEFAULT, 'Религия обитателей острова Наат. Сотня различных видов мотыльков порхает над островом, и жители Наата чтят их как посланцев Владыки, исполняющих поручение оберегать его народ', DEFAULT, 'Не наступило'),
  ('Религия Безупречных', DEFAULT, 'Безупречные поклоняются лишь одному божеству, настоящее имя которого известно только им', DEFAULT, 'Не наступило'),
  ('Религия Валирии', DEFAULT, 'О Валирийской религии изветсно очень мало, лишь то, что в Валирии существовал политеизм и пантеон божеств. Изветсны также имена некоторых богов', DEFAULT, '102 до З.Э.'),
  ('Религия Ленгийцов', DEFAULT, 'Некогда Ленгийцы веровали в своих «Древних Богов», что якобы обитали в подземных лабиринтах, которые пронизывают остров Лэнг. Ныне же они поклоняются своей императрице, которая почитается словно богиня', DEFAULT, 'Не наступило');
