INSERT INTO Типы_действ_лиц (Название, ИД_религии, Справ_информация, Время_появления, Время_исчезновения) VALUES
  ('Первые люди', 1, 'Первые люди — один из народов, населявших Вестерос в древности. Согласно преданиям, они были действительно первыми людьми, поселившимися в Вестеросе', DEFAULT, DEFAULT), -- 1
  ('Андалы', 2, 'Андалы — существовавший в древности народ, внесший свой вклад в историю Вестероса. Несколько тысяч лет назад, когда Вестерос населяли Первые Люди и Дети Леса, воинственный народ андалов пересек Узкое море и завоевал большую часть континента, разрушив старые королевства Первых Людей', DEFAULT, DEFAULT), -- 2
  ('Черноухие', DEFAULT, 'Черноухие - горный клан, получивший свое прозвище за привычку резать уши врагов и носить их в качестве трофеев. Они имеют тесную связь с Лунными Братьями', DEFAULT, 'Не наступило'), -- 3
  ('Обгорелые', DEFAULT, 'Обгорелые - горный клан, получивший свое прозвище благодаря обряду инициации. В определенном возрасте каждый член этого клана должен самостоятельно нанести себе ожог, и чем серьезнее он был, тем выше уважение они получали от соплеменников', DEFAULT, 'Не наступило'), -- 4
  ('Каменные Вороны', DEFAULT, 'Каменные Вороны - один из самых сильных кланов в Долине. Их предводитель Шагга, а до него Дольф.', DEFAULT, 'Не наступило'), -- 5
  ('Раскрашенные Псы', DEFAULT, 'Раскрашенные Псы - один из горных кланов в Долине', DEFAULT, 'Не наступило'), -- 6
  ('Молочные Змеи', DEFAULT, 'Молочные Змеи - один из горных кланов в Долине', DEFAULT, 'Не наступило'),-- 7
  ('Сыны Тумана', DEFAULT, 'Сыны Тумана - один из горных кланов в Долине', DEFAULT, 'Не наступило'), -- 8
  ('Сыны Древа', DEFAULT, 'Сыны Древа - один из горных кланов в Долине', DEFAULT, 'Не наступило'),
  ('Ревуны', DEFAULT, 'Ревуны - один из горных кланов в Долине', DEFAULT, 'Не наступило'),
  ('Багровые Кузнецы', DEFAULT, 'Багровые Кузнецы - один из горных кланов в Долине', DEFAULT, 'Не наступило'),
  ('Дорнийцы', 2, 'Дорнийцы — один из народов Вестероса, жители Дорна, в чьих жилах течет кровь когда-то переселившихся в эти края ройнаров, мало похожи на уроженцев соседних областей. В других областях страны дорнийцы известны горячим темпераментом и сексуальной распущенностью', DEFAULT, 'Не наступило'),
  ('Северяне', 1, 'Северяне — это один из народов современного Вестероса, обитатели Севера', DEFAULT, 'Не наступило'),
  ('Озёрные жители', 1, 'Озёрные жители — обитатели озер и болот Перешейка. Хотя они и присягают на верность Старкам из Винтерфелла, это скрытный низкорослый народец, мало похожий на других северян', DEFAULT, 'Не наступило'),
  ('Тенны', 1, 'Тенны — известный своей воинственностью клан одичалых родом из Теннии, затерянной долины среди Клыков Мороза', DEFAULT, 'Не наступило'),
  ('Рогоногие', 1, 'Рогоногие – одно из племен, проживающих за Стеной. Ороговевшие подошвы их ног были настолько невосприимчивы к холоду, что они ходили по снегу босиком и не нуждались в обуви', DEFAULT, 'Не наступило'),
  ('Людоеды', 1, 'Людоеды — одно из племен одичалых, живущее на вечно замерзших реках Севера по соседству с долиной теннов', DEFAULT, 'Не наступило'),
  ('Полуночники', 1, 'Полуночники — одно из племен одичалых', DEFAULT, 'Не наступило'),
  ('Пещерные жители', 1, 'Пещерные жители — одно из племен одичалых, живущее в пещерах по соседству с долиной теннов', DEFAULT, 'Не наступило'),
  ('Дети Леса', 1, 'Дети Леса — таинственный нечеловеческий народ, населявший Вестерос в Век Зари, до появления Первых Людей двенадцать тысяч лет назад', DEFAULT, 'Не наступило'), -- 20
  ('Иные', DEFAULT, 'Иные — полулегендарные чудовищные создания, обитающие в землях за Стеной', DEFAULT, 'Не наступило'), -- 21
  ('Упыри', DEFAULT, 'Упыри — оживленные Иными трупы животных и людей; восставшие мертвецы, «зомби». Это молчаливые, зловещие существа, которых очень тяжело вторично убить', DEFAULT, 'Не наступило'), -- 22
  ('Великаны', DEFAULT, 'Великаны — древняя раса, представители которой жили в Вестеросе до появления Первых Людей. Как и Дети Леса, со временем они ушли на дальний Север, а после оказались по ту сторону Стены', DEFAULT, 'Не наступило'),
  ('Валирийцы', 14, 'Валирийцы — некогда великий народ, проживающий на полуострове Валирия и многочисленных колониях, большая часть погибла после Рока, немногочисленные потомки обитают в Вольных Городах', DEFAULT, 'Не наступило'),
  ('Ройнары', 11, 'Ройнары — один из древнейших народов Вестероса. Первоначально народ, населявший некогда города, расположенные на берегу реки Ройны, название которой и дало им имя', DEFAULT, 'Еще не наступило'),
  ('Дотракийцы', DEFAULT, 'Дотракийцы — один из народов Эссоса. Это кочевой народ, не имеющий постоянных поселений, кроме Вейес Дотрак, и кочующий по бескрайним степям так называемого Дотракийского моря', DEFAULT, 'Еще не наступило'),
  ('Гискарцы', DEFAULT, 'Гискарцы — народ, проживающий в области Гискар, континента Эссос. Созданная этим народом Гискарская империя, была одним из самых могущественных государств в Эссосе, так продолжалось до конфликта с Валирией', DEFAULT, 'Не наступило'),
  ('Иббенийцы', DEFAULT, 'Иббенийцы — народ, населяющий Иббен, архипелаг в Студеном море к северу от Эссоса. Это коренастые, низкорослые, очень волосатые люди с отталкивающей звероподобной внешностью', DEFAULT, 'Не наступило'),
  ('Кваатийцы', DEFAULT, 'Кваатийцы — древний народ, некогда обитавший в Эссосе. В настоящее время кваатийцы почти исчезли с лица земли, от всех их древних царств остался только один торговый город-государство Кварт.', DEFAULT, 'Не наступило'),
  ('Народ Летних островов', DEFAULT, 'Чернокожие люди, которые говорят на своём языке, а в одежде предпочитают плащи из разноцветных перьев. Акт любви они считают очень важным и даже святым делом', DEFAULT, 'Не наступило'),
  ('Высокие люди', DEFAULT, 'Высокие люди — народ, проживающий в Эссосе. На пике своего могущества возводили свои города на всех землях, что омывались рекой Сарной, её притоками и тремя озёрами, что остались от некогда существовавшего здесь Серебряного моря', DEFAULT, 'Не наступило'),
  ('Жители Жабьего острова', DEFAULT, 'Жители Жабьего острова — люди живущие на Жабьем острове, одном из островов Василиска', DEFAULT, 'Не наступило'),
  ('Жители Тысячи Островов', DEFAULT, 'Жители Тысячи Островов — раса, живущая на Тысяче Островов. Это безволосые люди с кожей зеленоватого оттенка, которые заостряют зубы у своих женщин и обрезают крайнюю плоть у своих мужчин', DEFAULT, 'Не наступило'),
  ('Итийцы', 8, DEFAULT, DEFAULT, 'Не наступило'),
  ('Ленгийцы', 15, 'Ленгийцы — коренные жители острова Лэнг. Ленгийцы редко покидают родные земли, они не проявляют интереса к чужеземным богам и товарам, что привозят торговцы из далеких земель', DEFAULT, 'Не наступило'),
  ('Пятнистые люди', DEFAULT, 'Пятнистые люди преимущественно обитают в джунглях, некоторые селятся у побережья материка', DEFAULT, 'Не наступило'),
  ('Кентавры', DEFAULT, 'Кентавры - нечеловеческий народ, проживавший к востоку от Сарнора. Кентавры представляли из себя наполовину людей и наполовину коней', DEFAULT, DEFAULT),
  ('Шрайки', DEFAULT, 'Шрайки — полусказочные создания, обитающие в Серой Пустоши, в землях, которые соседи называют Землями шрайков. Чтобы защитить империю И Ти от шрайков, на границе с их землями возведены Пять крепостей', DEFAULT, 'Не наступило'),
  ('Волосатые люди', DEFAULT, 'Волосатые люди — раса, жившая в Рассветную эпоху на территории Степей. Волосатые люди были лохматы и размерами немногим превосходили человека', 'Рассветная эпоха', 'Рассветная эпоха'),
  ('Строители лабиринтов', DEFAULT, 'Строители лабиринтов — раса, некогда обитавшая в Лорате и островах рядом с ним. Строители были крупнее обычных людей, но меньше великанов', DEFAULT, DEFAULT),
  ('Джогвин', DEFAULT, 'Джогвин — раса, тысячу лет назад обитавшая в Стране Джогвин. По слухам они были вдвое крупнее великанов', DEFAULT, DEFAULT),
  ('Наатийцы', 12, 'Наатийцы миролюбивый народ, войне, они предпочитают музыку и танцы. Даже в целях самообороны они не поднимут оружия, что делает их легкой добычей для пиратов и работорговцев', DEFAULT, 'Не наступило'),
  ('Глубоководные', DEFAULT, 'Глубоководные — нечеловеческая раса, возможно, обитавшая в море у берегов Эссоса и Вестероса. Память о ней может сохраняться в сказках о водяных', DEFAULT, DEFAULT),
  ('Волантийцы', 4, 'Чистокровные жители Старого Волантиса, как и лиссенийцы, голубоглазы и светлокожи, но в городе много рабов — представителей всех рас и народов. В Волантисе принято татуировать лица рабов и слуг', DEFAULT, 'Не наступило'),
  ('Браавосийцы', DEFAULT, 'Жители Браавоса самых разных культур и религий', DEFAULT, 'Не наступило'),
  ('Мирийцы', DEFAULT, 'Мирийцы славятся своими ремесленниками. Некоторые мейстеры считают, что здешние жители родственны ройнарам, ибо у них такая же оливковая кожа, да и волосы темных оттенков, но многие оспаривают эту точку зрения', DEFAULT, 'Не наступило'),
  ('Лиснийцы', 4, 'Лисенийцы — очень красивый народ, со светлой кожей, практически белыми «льняными» волосами и голубыми глазами. Здесь немало людей и с валирийской внешностью, такой же, как и у Таргариенов или жителей Старого Волантиса', DEFAULT, 'Не наступило'),
  ('Пентошийцы', DEFAULT, 'Валирийны, предшие в гоород были низкого происхождения и мало заботились о чистоте своей крови, так что в теперешних жителях Пентоса отчетлива видна андальская кровь, что делает их сродни вестеросцам. Мужчины в городе носят крашенные и разделенные надвое бороды', DEFAULT, 'Не наступило'),
  ('Квохоряне', 10, 'Квохоряне — купцы, а не воины. Среди квохорян много умелых ремесленников', DEFAULT, 'Не наступило'),
  ('Тирошийцы', DEFAULT, 'Тирошийских наемных солдат и купцов — в том числе и работорговцев — можно встретить по всему миру, в том числе и в Вестеросе; их легко узнать, так как жители Тироша традиционно красят волосы и бороды в необычные яркие цвета, например, зеленый или синий', DEFAULT, 'Не наступило'),
  ('Лоратийцы', DEFAULT, 'Лоратийцы состоят из рыбаков, приезжих с Иба, Андалоса и других стран, даже беглых рабов из Валирии', DEFAULT, 'Не наступило'),
  ('Кланы Лунных гор', DEFAULT, 'Дикие горные племена, обитающие в Лунных горах на территории Долины.', DEFAULT, 'Не наступило');
INSERT INTO Дома (Название, ИД_сюзерена, ИД_религии, Описание_герба, Девиз, Справ_информация, Великий_ли, Время_появления, Время_исчезновения) VALUES
  ('Старки', DEFAULT, 1, 'Серый лютоволк, бегущий по снежно-белому полю', 'Зима близко', 'Старки — один из великих домов Вестероса, правители Севера', true, 'Век Героев', 'Не наступило'), -- 53
  ('Баратеоны', DEFAULT, 2, 'Чёрный коронованный олень на золотом поле', 'Нам ярость', 'Баратеоны — один из великих домов Вестероса. Они правят Штормовыми землями', true, '1/?/? от З.Э.', 'Не наступило'), -- 54
  ('Ланнистеры', DEFAULT, 2, 'Золотой лев на алом поле', 'Услышь мой рёв!', 'Ланнистеры — один из великих домов Вестероса, правители Западных Земель', true, 'Век Героев', 'Не наступило'), -- 55
  ('Таргариены', DEFAULT, 2, 'Красный трёхглавый дракон на чёрном поле', 'Пламя и кровь', 'Таргариены — королевская династия, ранее правившая Семью Королевствами. Таргариены были одной из аристократических семей Валирии, умевших повелевать драконами; согласно преданиям, в их жилах текла драконья кровь', true, DEFAULT, 'Не наступило'), -- 56
  ('Аррены', DEFAULT, 2, 'Белая луна и сокол на небесно-синем поле', 'Высокий как честь', 'Аррены — один из великих домов Вестероса, правители Долины Аррен', true, DEFAULT, 'Не наступило'), -- 57
