INSERT INTO Организации (Название, ИД_вида, ИД_религии, Цель, Справ_информация, Время_появления, Время_исчезновения) VALUES
  ('Ночной дозор', 1, DEFAULT, 'Охрана Семи Королевств от угрозы с севера', 'Ночной Дозор — военная организация, древний орден, призванный нести стражу на Стене, поддерживать её сохранность и охранять Семь Королевств от угрозы с севера', 'Долгая Ночь', 'Не наступило'),
  ('Королевская гвардия', 1, DEFAULT, 'Охрана лиц королевской крови', 'Королевская гвардия — орден, созданный сестрой Эйгона I Таргариена - Висеньей Таргариен для охраны лиц королевской крови. Гвардия состоит из семи рыцарей, принесших присягу верой и правдой служить королю до самой своей смерти.', '10/?/? от З.Э.', 'Не наступило'),
  ('Радужная гвардия', '1', DEFAULT, 'Личная охрана Ренли Баратеона, Короля в Хайгардене', 'Орден, состоящий из 7 рыцарей по цветам радуги, личная охрана Ренли Баратеона, Короля в Хайгардене. Радужная Гвардия была создана по образу и подобию Королевской Гвардии, защитников королей на Железном Троне.', '299/?/? от З.Э.', 'Не наступило'),
  ('Святое воинство', '1', DEFAULT, 'Защита Веры в Семерых', 'Вооруженная ветвь Веры в Семерых, военно-духовные ордены, присягнувшие мечом делу защиты веры. Святое Воинство включало в себя два ордена: Сыны Воина («Мечи») принимали в свои ряды рыцарей и сыновей лордов, Честные Бедняки («Звезды») были открыты для простого народа.', DEFAULT, '48/?/? от З.Э.'),
  ('Золотые мечи', '2', DEFAULT, 'Объединение изгнанников', 'Большой наемный отряд, действующий на территории Вольных Городов. Он состоит в основном из эмигрантов из Вестероса и потомков эмигрантов; в его рядах много рыцарей и бывших лордов, питающих надежды на возвращение домой.', '200 от З.Э.', 'Не наступило'),
  ('Бравые ребята', '2', DEFAULT, 'Оказание услуг наёмников', 'Отряд профессиональных наемников из Вольных Городов. Репутацию имеют крайне скверную.', DEFAULT, '299 от З.Э.'),
  ('Младшие сыновья', '2', DEFAULT, 'Защита от Дейенерис', 'Отряд наемников из Вольных Городов. Были наняты Юнкаем с целью защиты от Дейнерис, впоследствии перешли на ее сторону. Знамя — рука со сломанным мечом', '200 от З.Э.', 'Не наступило'),
  ('Вороны-Буревестники', '2', DEFAULT, 'Защита от Дейенерис', 'Вороны-Буревестники — отряд наемников, основанный в вольных городах.', DEFAULT, 'Не наступило'),
  ('Септоны', '3', '2', 'Поклонение Семи Богам', 'Септоны и септы — священнослужители Веры в Семерых, посвятившие свою жизнь религии. Они молятся семи богам андалов, проводят богослужения и проповеди.', DEFAULT, 'Не наступило'),
  ('Праведные', '3', '2', 'Выбор Верховного Септона', 'Праведные — высшее духовенство церкви Семерых, выбирающее Верховного Септона. Как правило главой церкви становится один из праведных, хотя бывают и исключения', DEFAULT, 'Не наступило'),
  ('Красные жрецы', '3', '4', 'Поклонение Рглору', 'Красные жрецы и жрицы — священнослужители веры в Рглора, Владыку Света. Жрецами Рглора могут быть как мужчины, так и женщины; они носят просторные ярко-красные одеяния', DEFAULT, 'Не наступило'),
  ('Малый совет', '4', DEFAULT, 'Обсуждение государственных дел Вестероса', 'Малый совет — коллегиальный орган власти при королях Вестероса. По андальскому обычаю состоял из семи лордов-сановников, назначаемых королём', DEFAULT, 'Не наступило'),
  ('Мейстеры', '5', DEFAULT, 'Объединяет ученных Вестероса', 'Практически в каждом замке Семи королевств есть свой учёный мейстер. Мейстеры выполняют роль наставников, советников, врачей и хранителей знания', DEFAULT, 'Не наступило'),
  ('Железный банк', '6', DEFAULT, 'Оказание услуг банка', 'Самый крупный и самый влиятельный банк Браавоса. Банк очень богат и часто дает взаймы разным людям, среди его клиентов есть и король Семи Королевств', DEFAULT, 'Не наступило'),
