INSERT INTO События (Название, ИД_вида, Время_начала, Время_окончания, Причина, Описание, Итог) VALUES
  ('Вторжение Первых Людей в Вестерос', 2, 'Рассветная эпоха', 'Рассветная эпоха', DEFAULT, 'Первые Люди пришли из Эссоса через существовавший тогда перешеек, соединявший два материка — Руку Дорна', 'Начало войны между Первыми Людьми и Детьми Леса'), -- 1
  ('Война между Первым Людьми и Детьми Леса', 3, 'Рассветная эпоха', 'Рассветная эпоха', 'Первые Люди начали вырубать священные леса для своих полей и крепостей, что вызвало у Детей Леса ужас', 'С помощью магии Дети Леса вызывали природные катастрофы, в частности, раздробив Руку Дорна и превратив ее в разрозненные острова Ступени', 'Заключение договора Острова Ликов'), -- 2
  ('Заключение договора Острова Ликов', 5, 'Рассветная эпоха', 'Рассветная эпоха', 'Мудрецы обоих народов (Первых людей и Детей Леса) призвали к миру', 'Представители обоих народов заключили договор на острове посреди озера, которое ныне называют Божьим оком', 'Дети Леса передали Первым Людям большую часть континента — побережья, равнины и горы, оставив себе леса, но запретив вырубать чардрева'), -- 3
  ('Началась Долгая Ночь', 6, 'Рассветная эпоха', 'Долгая Ночь', DEFAULT, 'Длинная холодная зима обратилась в Долгую Ночь, когда солнце не всходило много лет - целое поколение', DEFAULT), -- 4
  ('Вторжение Иных в королевства людей', 2, 'Долгая Ночь', 'Долгая Ночь', DEFAULT, 'Армия Иных вторглась в королевства людей, неся с собой мороз и смерть', 'Первые Люди в союзе с Детьми Леса одержали победу. На Севере Брандон Строитель с помощью великанов построил грандиозную Стену. Орден Ночной Дозор начал охранять Стену - чтобы не пропустить Иных, если они вновь вернутся'), -- 5
  ('Рассветная Битва', 4, 'Долгая Ночь', 'Долгая Ночь', 'Выступление Азора Ахая против Иных', 'Рассветная Битва — легендарная битва между Ночным Дозором и Иными в конце Долгой Ночи', 'Конец Долгой Ночи, изгнание Иных на крайний север'), -- 6
  ('Строительство Стены', 7, 'Век Героев', 'Век Героев', 'Вторжение Армии Иных', 'На Севере Брандон Строитель с помощью великанов построил грандиозную Стену', 'Орден Ночной Дозор, бившийся против Иных в Рассветной Битве, начал охранять Стену - чтобы не пропустить Иных, если они вновь вернутся'), -- 7
  ('Строительство Винтерфелла', 7, 'Век Героев', 'Век Героев', DEFAULT, 'По преданиям, построен легендарным Брандоном Строителем тысячи лет назад', DEFAULT), -- 8
  ('Основание Династии Старков', 8, 'Век Героев', 'Век Героев', DEFAULT, DEFAULT, DEFAULT), -- 9
  ('Строительство Стены', 7, 'Век Героев', 'Век Героев', 'Вторжение Армии Иных', 'На Севере Брандон Строитель с помощью великанов построил грандиозную Стену', 'Орден Ночной Дозор, бившийся против Иных в Рассветной Битве, начал охранять Стену - чтобы не пропустить Иных, если они вновь вернутся'), -- 10
  ('Восстание Роберта Баратеона', 1, '282/?/? От З.Э.', '284/?/? От З.Э.', 'Последний король из династии Таргариенов, Эйрис II Безумный, к концу жизни превратился в неуправляемого тирана', 'Вооруженное восстание многих знатных домов Семи Королевств, произошедшее за пятнадцать лет до начала событий книг.', 'Свержение династии Таргариенов и их изгнание из Вестероса'), -- 4
  ('Вторжение андалов', 2, 'Вторжение андалов', 'Вторжение андалов', 'Пророчество Семерых первому королю андалов - Хугору', 'Андалы, вооруженные стальным оружием и фанатично преданные своей вере в Семерых, пересекли Узкое море, обрушились на королевства Первых Людей и в течение нескольких сотен лет завоевали их одно за другим', 'Юг Вестероса стал андальским'), -- 6
